#include<bits/stdc++.h>using namespace std;#define IOS ios::sync_with_stdio(0); cin.tie(0); cout.tie(0);#define frfr  freopen("input.txt","r",stdin); //freopen("output.txt","w",stdout);#define endl "\n"#define loop(i,n) for(int i=0;i<n;i++)#define ll long long intvoid solve(){	int x1,y1,x2,y2,x3,y3;	cin >> x1 >> y1 >> x2 >> y2 >> x3 >> y3;	int ans = abs(x1-x2) + abs(y1-y2);	int count = 0;	if( x1 == x2 && y1 == y2){		cout << 0 << endl;return;	}	if( x2 > x1 ){		//goes  left;		//int count = 0 ;		while(x1 != x2){			if(x1 == x3 && y1 == y3){				count += 3;			}else{				count++;			}			x1++;		}		if( y2 > y1){			while(y1 != y2){				if(y1 == y3 && x1 == x3){					count += 3;				}else{					count++;				}				y1++;			}		}else{			while(y1 != y2){				if(y1 == y3 && x1 == x3){					count += 3;				}else{					count++;				}				y1--;			}		}		cout << count << endl;	}else{		while(x1 != x2){			if(x1 == x3 && y1 ==y3){				count += 3;			}else{				count ++;			}			x1--;		}		if( y2 > y1){			while(y1 != y2 ){				if(y1 == y3 && x1 == x3){					count += 3;				}				y1++;			}		}else{			while(y1 != y2){				if(y1 == y3 && x1 == x3){					count += 3;				}else{					count++;				}				y1--;			}		}		cout << count << endl;	}	}int main(){	IOS;			int te;	cin >> te;	while(te--){		solve();	}	return 0;}